import React, { useState } from "react";
import Footer from './Footer';

const Contanct =()=>{
    // useState for this data display
  const [ data ,setData ] = useState({
    name:"",
    number:"",
    email:"",
    about:""
  });

 //   change event to previous data and vakue input
  const inputEvent=(event)=>{
       const {name , value} =event.target                                                          ;

        // changed value to used
        setData((preVal)=>{
            return {
                ...preVal,
               [name]:value,
            };
        });
    };
  
    const inputButton=()=>{
        // e.preventDefault();
        
        // alert to display san submit data
        alert(`My Name ${data.name} my mobile No ${data.number} email numebr is ${data.email} and about of this ${data.about} `)
        
        // can submit data page refesh
        setData({
              name:"",
              number:"",
              email:"",
              about:""
            }
      
        )
  }

    return(
        <>
          <div className="my-5">
             <h1 className="text-center">Contanct Us </h1>
          </div>
          <div className="container contact_div">
             <div className="row">
                    <div className="col-md-6 col-10 mx-auto">
                        <div className="mb-2">
                            <label htmlFor="exampleFormControlInput1" className="form-label">Full Name</label>
                            <input type="text" className="form-control" name="name" value={data.name} onChange={inputEvent} id="exampleFormControlInput1 " placeholder="Enter Your Full Name"/>
                        </div>
                        <div className="mb-2">
                            <label htmlFor="exampleFormControlInput1" className="form-label">Mobile number</label>
                            <input type="number" className="form-control" name="number" value={data.number} onChange={inputEvent} id="exampleFormControlInput1 " placeholder="Enter your Mobile Number"/>
                        </div>
                        <div className="mb-2">
                            <label htmlFor="exampleFormControlInput1" className="form-label">Email address</label>
                            <input type="email" className="form-control" name="email" value={data.email} onChange={inputEvent} id="exampleFormControlInput1 " placeholder="name@example.com"/>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="exampleFormControlTextarea1" className="form-label">About Your</label>
                            <textarea className="form-control" name="about" value={data.about} placeholder="Enter your about" onChange={inputEvent} id="exampleFormControlTextarea1" rows="2"></textarea>
                        </div>
                        <div className="col-12" onClick={inputButton}>
                            <button className="btn btn-outline-primary " type="submit">Submit form</button>
                        </div>
                    </div>
                </div>
          </div>
          <Footer/>
        </>
    )
}


export default Contanct;
